export interface IDimensionsProps {
    /**
     * Define minimum width
     */
    minWidth?: string;
    /**
     * Define maximum width
     */
    maxWidth?: string;
    /**
     * Define element width
     */
    width?: string;
    /**
     * Define minimum height
     */
    minHeight?: string;
    /**
     * Define maximum height
     */
    maxHeight?: string;
    /**
     * Define element height
     */
    height?: string;
    /**
     * Set element padding
     */
    padding?: string;
    /**
     * Set element margin
     */
    margin?: string;
    /**
     * Should element fill all available width
     */
    isFullWidth?: boolean;
    /**
     * Define element border radius
     */
    borderRadius?: string;
    /**
     * Define element border
     */
    border?: string;
    /**
     * Define element borderc color
     */
    borderGradientColor?: string;
}
